// ссылки
int value = 7; // обычная переменная
int &ref = value; // ссылка на переменную value

////////

int value = 7; // обычная переменная
int &ref = value; // ссылка на переменную value
value = 8; // value теперь 8
ref = 9; // value теперь 9
std::cout << value << std::endl; // выведется 9
++ref;
std::cout << value << std::endl; // выведется 10

