// Ссылки в качестве параметров в функциях
#include <iostream>
// ref - это ссылка на переданный аргумент, а не копия аргумента
void changeN(int &ref)
{
    ref = 8;
}
int main()
{
    int x = 7;
    std::cout << x << '\n';
    changeN(x); // обратите внимание, этот аргумент не обязательно должен быть ссылкой
    std::cout << x << '\n';
    return 0;
}

